// 2. Referências aos elementos HTML (estas devem ser obtidas APENAS quando o DOM estiver pronto)
// As declarações abaixo ficarão dentro de uma função ou listener.

let lobSelect, areaSelect, produtoSelect, topicoSelect, subtopicoSelect, resultado;
let dialogSubtopico, inputFrase, btnOk, btnCancelar; // Para o diálogo do subtopic

// 3. Função de inicialização para carregar os LOBs e configurar listeners
function inicializarTaxonomia() {
    // Obtenha as referências aos elementos HTML aqui, quando o DOM já está pronto
    lobSelect = document.getElementById("lob");
    areaSelect = document.getElementById("area");
    produtoSelect = document.getElementById("produtoTaxonomia");
    topicoSelect = document.getElementById("topico");
    subtopicoSelect = document.getElementById("subtopico");
    resultado = document.getElementById("resultado");

    dialogSubtopico = document.getElementById("dialogSubtopico");
    inputFrase = document.getElementById("inputFrase");
    btnOk = document.getElementById("btnOk");
    btnCancelar = document.getElementById("btnCancelar");

    // Preencher o dropdown de LOBs
    const lobs = [...new Set(dados.map(item => item["LOB"]))];
    lobs.sort().forEach(function(lob) {
        const option = document.createElement("option");
        option.value = lob;
        option.textContent = lob;
        lobSelect.appendChild(option);
    });

    // Adicionar event listeners aos selects
    lobSelect.addEventListener('change', atualizarAreas);
    areaSelect.addEventListener('change', atualizarProdutos);
    produtoSelect.addEventListener('change', atualizarTopicos);
    topicoSelect.addEventListener('change', atualizarSubtopicos);
    subtopicoSelect.addEventListener('change', mostrarResultado); // Adicionado para atualizar o resultado ao mudar subtopico
    subtopicoSelect.addEventListener("change", abrirAdialog); // Para o diálogo específico

    // Listeners para os botões do diálogo do subtópico
    btnOk.addEventListener("click", function () {
        const frase = inputFrase.value.trim();
        if (frase !== "") {
            const novaOpcao = new Option(frase, frase);
            subtopicoSelect.add(novaOpcao);
            subtopicoSelect.value = frase;
            mostrarResultado();
        }
        inputFrase.value = "";
        dialogSubtopico.close(); // Use dialogSubtopico aqui
    });

    btnCancelar.addEventListener("click", function () {
        dialogSubtopico.close(); // Use dialogSubtopico aqui
    });

    // Listener para o botão de Copiar
    document.querySelector('.box-buttons button:nth-child(2)').addEventListener('click', copiar); // Assume que 'Copiar' é o segundo botão
    // Listener para o botão de Reset
    document.querySelector('.box-buttons button:nth-child(1)').addEventListener('click', resetar); // Assume que 'Reset' é o primeiro botão

    // Adicionado para o evento de change do produto, se houver lógica de redirecionamento
    produtoSelect.addEventListener("change", atualizarRedirecionamento);
}

// 4. Chamar a função de inicialização quando o DOM estiver completamente carregado
document.addEventListener('DOMContentLoaded', inicializarTaxonomia);

// 5. Mantenha as funções de atualização e outras lógicas como estão
function atualizarAreas() {
    areaSelect.innerHTML = '<option value="">Selecione</option>';
    produtoSelect.innerHTML = '<option value="">Selecione</option>';
    topicoSelect.innerHTML = '<option value="">Selecione</option>';
    subtopicoSelect.innerHTML = '<option value="">Selecione</option>';
    resultado.textContent = `${lobSelect.value} | `; // Use textContent

    const lobSelecionado = lobSelect.value;
    const areas = [...new Set(dados.filter(item => item["LOB"] === lobSelecionado).map(item => item["Product Area"]))];
    areas.sort().forEach(function(area) {
        const option = document.createElement("option");
        option.value = area;
        option.textContent = area;
        areaSelect.appendChild(option);
    });
}

function atualizarProdutos() {
    produtoSelect.innerHTML = '<option value="">Selecione</option>';
    topicoSelect.innerHTML = '<option value="">Selecione</option>';
    subtopicoSelect.innerHTML = '<option value="">Selecione</option>';
    resultado.textContent = `${lobSelect.value} | ${areaSelect.value}`; // Use textContent

    const lobSelecionado = lobSelect.value;
    const areaSelecionada = areaSelect.value;
    const produtos = [...new Set(dados.filter(item =>
        item["LOB"] === lobSelecionado && item["Product Area"] === areaSelecionada).map(item => item["Product"]))];
    produtos.sort().forEach(function(produto) {
        const option = document.createElement("option");
        option.value = produto;
        option.textContent = produto;
        produtoSelect.appendChild(option);
    });
}

function atualizarTopicos() {
    topicoSelect.innerHTML = '<option value="">Selecione</option>';
    subtopicoSelect.innerHTML = '<option value="">Selecione</option>';
    resultado.textContent = `${lobSelect.value} | ${areaSelect.value} | ${produtoSelect.value} `; // Use textContent

    const lobSelecionado = lobSelect.value;
    const areaSelecionada = areaSelect.value;
    const produtoSelecionado = produtoSelect.value;
    const topicos = [...new Set(dados.filter(item =>
        item["LOB"] === lobSelecionado &&
        item["Product Area"] === areaSelecionada &&
        item["Product"] === produtoSelecionado
    ).map(item => item["Topic"]))];
    topicos.sort().forEach(function(topico) {
        const option = document.createElement("option");
        option.value = topico;
        option.textContent = topico || "(vazio)";
        topicoSelect.appendChild(option);
    });
}

function atualizarSubtopicos() {
    subtopicoSelect.innerHTML = '<option value="">Selecione</option>';
    resultado.textContent = `${lobSelect.value} | ${areaSelect.value} | ${produtoSelect.value} | ${topicoSelect.value} `; // Use textContent

    const lobSelecionado = lobSelect.value;
    const areaSelecionada = areaSelect.value;
    const produtoSelecionado = produtoSelect.value;
    const topicoSelecionado = topicoSelect.value;
    const subtopicos = [...new Set(dados.filter(item =>
        item["LOB"] === lobSelecionado &&
        item["Product Area"] === areaSelecionada &&
        item["Product"] === produtoSelecionado &&
        item["Topic"] === topicoSelecionado
    ).map(item => item["Subtopic"]))];
    subtopicos.sort().forEach(function(subtopico) {
        const option = document.createElement("option");
        option.value = subtopico;
        option.textContent = subtopico || "(vazio)";
        subtopicoSelect.appendChild(option);
    });
    // Chamada do mostrarResultado() aqui para garantir que o resultado seja atualizado
    // antes de um possível diálogo.
    mostrarResultado();
}


function mostrarResultado() {
    const lob = lobSelect.value;
    const area = areaSelect.value;
    const produto = produtoSelect.value;
    const topico = topicoSelect.value;
    const subtopico = subtopicoSelect.value;
    resultado.textContent = `${lob} | ${area} | ${produto} | ${topico} | ${subtopico}`;
    // Chame a função de redirecionamento aqui para que ela seja acionada
    // a cada atualização de resultado (ou você pode mover para o change do ultimo select).
    atualizarRedirecionamento();
}

function resetar() {
    lobSelect.value = "";
    areaSelect.innerHTML = '<option value="">Selecione</option>';
    produtoSelect.innerHTML = '<option value="">Selecione</option>';
    topicoSelect.innerHTML = '<option value="">Selecione</option>';
    subtopicoSelect.innerHTML = '<option value="">Selecione</option>';
    resultado.textContent = "";
    // Recarregar os LOBs após o reset para que o primeiro dropdown não fique vazio
    const lobs = [...new Set(dados.map(item => item["LOB"]))];
    lobs.sort().forEach(function(lob) {
        const option = document.createElement("option");
        option.value = lob;
        option.textContent = lob;
        lobSelect.appendChild(option);
    });
}

function copiar() {
    const texto = resultado.textContent;
    navigator.clipboard.writeText(texto).then(function() {
        alert("Copiado para a área de transferência: " + texto);
    }).catch(err => {
        console.error('Erro ao copiar: ', err);
        alert('Falha ao copiar o texto. Por favor, copie manualmente.');
    });
}

// Funções de Diálogo e Redirecionamento
function abrirAdialog() {
    if (subtopicoSelect.value === "*Specific Scenario") {
        dialogSubtopico.showModal();
    }
}

function verificarRedirecionamento(area, produto, topico, subtopico, origem) {
    if (produto === "outlook.com" && origem === "MLTS") return "MLTS | outlook.com In-App Redirect";
    if (produto === "OneDrive" && origem === "MLTS") return "MLTS | OneDrive Redirect – Technical Issue";
    if (produto === "Commercial Support" && origem === "MLTS") return "MLTS | Transfer to Commercial or Enterprise Support";
    if (produto === "Skype" && origem === "MLTS") return "MLTS | Transfer to Skype Support";
    if (produto === "Surface" && origem === "MLTS") return "MLTS | Transfer to Surface Support";
    if (produto === "Xbox" && origem === "MLTS") return "MLTS | Transfer to XBOX Support";
    if (produto === "Store" && origem === "MLTS") return "MLTS | Redirect to Store";
    if (produto === "Ghost" && origem === "MLTS") return "MLTS | Ghost Call";
    if (produto === "MLCS" && origem === "MLTS" && area === "Other") return "MLTS | Redirect to MLCS";
    if (produto === "Financeira" && origem === "MLTS") return "MLTS | Redirect to Financial Institution";
    if (produto === "Terceiros" && origem === "MLTS") return "MLTS | Redirect to Third Parties";
    if (topico === "Transfer" && produto === "Microsoft Teams" && origem === "MLTS") return "MLTS | Redirect to Teams Support";
    if (produto === "outlook.com" && origem === "MLCS") return "MLCS | outlook.com In-App Redirect";
    if (produto === "Commercial Support" && origem === "MLCS") return "MLCS | Transfer to Commercial or Enterprise Support";
    if (produto === "Skype" && origem === "MLCS") return "MLCS | Transfer to Skype Support";
    if (area === "Other" && produto === "Surface" && origem === "MLCS") return "MLCS | Transfer to Surface Support";
    if (area === "Other" && produto === "OneDrive" && origem === "MLCS") return "MLCS | OneDrive Redirect - Technical Issue";
    if (produto === "Xbox" && origem === "MLCS") return "MLCS | Transfer to XBOX Support";
    if (produto === "Store" && origem === "MLCS") return "MLCS | Redirect to Store";
    if (produto === "MLTS" && origem === "MLCS" && area === "Other") return "MLCS | Redirect to MLTS";
    if (produto === "Financeira" && origem === "MLCS") return "MLCS | Redirect to Financial Institution";
    if (produto === "Ghost" && origem === "MLCS") return "MLCS | Ghost Call";
    if (produto === "Terceiros" && origem === "MLCS") return "MLCS | Redirect to Third Parties";
    if (topico === "Transfer" && produto === "Microsoft Teams" && origem === "MLCS") return "MLCS | Redirect to Teams Support";
    return "";
}

function atualizarRedirecionamento() {
    const area = areaSelect.value;
    const produto = produtoSelect.value;
    const topico = topicoSelect.value;
    const subtopico = subtopicoSelect.value;
    const origem = lobSelect.value;

    const novoResultado = verificarRedirecionamento(area, produto, topico, subtopico, origem);

    // Se houver um redirecionamento, substitui o resultado da taxonomia.
    // Caso contrário, mantém o resultado da taxonomia.
    if (novoResultado && novoResultado.trim() !== "") {
        resultado.textContent = novoResultado;
    } else {
        // Se não houver redirecionamento, garante que o resultado da taxonomia seja exibido
        // Isso é importante porque o `mostrarResultado` é chamado em `atualizarSubtopicos`
        // mas pode não ter sido chamado em outras funções se a seleção parar antes.
        mostrarResultado();
    }
}


